mapper.update:
UPDATE TBL_PHOTO
SET TITLE = #{title}, CONT = #{cont}
WHERE NO = #{no}

mapper.select:
SELECT COUNT(*) FROM TBL_PHOTO WHERE 1=1
AND TITLE LIKE '%'||#{keyword}||'%' OR CONT LIKE '%'||#{keyword}||'%'
AND CATEGORY = #{category}

mapper.update:
UPDATE TBL_PHOTO
SET READCOUNT = READCOUNT + 1
WHERE NO = #{no}
